import React, { useEffect, useState } from 'react';
import './DataFetcher.css';

const DataFetcher = () => {
  const [data, setData] = useState([]);
  const [filterBy, setFilter] = useState("revenue");
  const [minCriteria, setMinCriteria] = useState("");
  const [maxCriteria, setMaxCriteria] = useState("");
  const [sortBy, setSort] = useState("");
  const [sortDirection, setSortDirection] = useState("desc");
  const [selectedColumn, setSelectedColumn] = useState("");
  const columns = ["date", "revenue", "netIncome", "grossProfit", "eps", "operatingIncome"];

  const filterData = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/data?filter=${filterBy}&min=${minCriteria}&max=${maxCriteria}`
      );
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const resetData = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/data?`);
      const result = await response.json();
      setMinCriteria("");
      setMaxCriteria("");
      setFilter("revenue");
      setData(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="container">
      <h1 className="title">Apple Income Statements</h1>
      <div className="input-group">
        <select
          className="input"
          value={filterBy}
          onChange={(e) => setFilter(e.target.value)}
        >
          {columns.map((column) => (
            <option key={column} value={column}>
              {column}
            </option>
          ))}
        </select>
        <input
          type="number"
          className="input"
          placeholder="Min value"
          value={minCriteria}
          onChange={(e) => setMinCriteria(e.target.value)}
        />
        <input
          type="number"
          className="input"
          placeholder="Max value"
          value={maxCriteria}
          onChange={(e) => setMaxCriteria(e.target.value)}
        />
        <button className="button" onClick={filterData}>
          Filter
        </button>
        <button className="button" onClick={resetData}>
          Reset
        </button>
      </div>

      {data.length > 0 ? (
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                {columns.map((column) => (
                  <th
                    key={column}
                    onClick={() => {
                      setSort(column);
                      setSelectedColumn(column);
                      setSortDirection(sortDirection === "asc" ? "desc" : "asc");
                    }}
                  >
                    {column} {selectedColumn === column ? (sortDirection === "asc" ? "↑" : "↓") : ""}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={index}>
                  {columns.map((column) => (
                    <td key={column}>{item[column]}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No data found</p>
      )}
    </div>
  );
};

export default DataFetcher;
