import React, { useEffect, useState } from 'react';
import './DataFetcher.css'; // Import the CSS file

const DataFetcher = () => {
  const [data, setData] = useState([]);
  const [filterBy, setFilter] = useState();
  const [criteria, setCriteria] = useState('');
  const [sortBy, setSort] = useState('');
  const [direction, setDir] = useState('');
  const filterData = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/data?filter=${filterBy}&criteria=${criteria}`);
      const result = await response.json();
      setData(result);
      console.log(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  // useEffect(() => {
  //   if (sortBy) {
  //     sortData();
  //   }
  // }, [sortBy]);

  // useEffect(() => {
  //   fetchData();
  // }, [filter, criteria]);
  const sortData = async (column, dir) => {
    await setSort(column)
    await setDir(dir) 
    try { 
      const response = await fetch(`http://localhost:5000/api/data?sort=${sortBy}&order=${orderBy}`);
      const result = await response.json();
      setData(result);
      console.log(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }
  return (
    <div className="container">
      <h1 className="title">Fetched Data</h1>
      <div className="input-group">
        <input
          type="text"
          className="input"
          placeholder="Enter filter"
          value={filterBy}
          onChange={(e) => setFilter(e.target.value)}
        />
        <input
          type="text"
          className="input"
          placeholder="Enter criteria"
          value={criteria}
          onChange={(e) => setCriteria(e.target.value)}
        />
        <button className="button" onClick={filterData}>
          Fetch Data
        </button>
      </div>

      {data.length > 0 ? (
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th onClick={() => (sortData('date', 'asc'))}>Date</th>
                <th onClick={() => (sortData('revenue', 'asc'))}>Revenue</th>
                <th onClick={() => (sortData('netIncome', 'asc'))}>Net Income</th>
                <th onClick={() => (sortData('grossProfit', 'asc'))}>Gross Profit</th>
                <th onClick={() => (sortData('eps', 'asc'))}>EPS</th>
                <th onClick={() => (sortData('operatingIncome', 'asc'))}>Operating Income</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={index}>
                  <td>{item.date}</td>
                  <td>{item.revenue}</td>
                  <td>{item.netIncome}</td>
                  <td>{item.grossProfit}</td>
                  <td>{item.eps}</td>
                  <td>{item.operatingIncome}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No data found</p>
      )}
    </div>
  );
};

export default DataFetcher;
