import React, { useEffect, useState } from 'react';
import './DataFetcher.css';

const DataFetcher = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'descending' });

  // Fetch data from the API
  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/data');
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Sorting logic
  const sortData = (key) => {
    let direction = 'descending';
    if (sortConfig.key === key && sortConfig.direction === 'descending') {
      direction = 'ascending';
    }
    setSortConfig({ key, direction });

    const sortedData = [...data].sort((a, b) => {
      if (a[key] < b[key]) {
        return direction === 'ascending' ? -1 : 1;
      }
      if (a[key] > b[key]) {
        return direction === 'ascending' ? 1 : -1;
      }
      return 0;
    });

    setData(sortedData);
  };

  // Table headers with sorting
  const headers = [
    { label: 'Date', key: 'date' },
    { label: 'Revenue', key: 'revenue' },
    { label: 'Net Income', key: 'netIncome' },
    { label: 'Gross Profit', key: 'grossProfit' },
    { label: 'EPS', key: 'eps' },
    { label: 'Operating Income', key: 'operatingIncome' },
  ];

  return (
    <div className="container">
      <h1 className="title">Apple Income Statements</h1>
      {loading ? (
        <p>Loading data...</p>
      ) : data.length > 0 ? (
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                {headers.map((header) => (
                  <th
                    key={header.key}
                    onClick={() => sortData(header.key)}
                    className={`sortable ${
                      sortConfig.key === header.key ? sortConfig.direction : ''
                    }`}
                  >
                    {header.label}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={index}>
                  <td>{item.date}</td>
                  <td>{item.revenue}</td>
                  <td>{item.netIncome}</td>
                  <td>{item.grossProfit}</td>
                  <td>{item.eps}</td>
                  <td>{item.operatingIncome}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default DataFetcher;
